import postcss from 'postcss';
import test    from 'ava';

import plugin from './';

function run(t, input, output, opts = { }) {
    return postcss([ plugin(opts.filter, opts.wrapper) ]).process(input)
        .then( result => {
            t.same(result.css, output);
            t.same(result.warnings().length, 0);
        });
}

test('does nothing', t => {
    return run(t, 'a{ }', 'a{ }', {
        filter:  '**/vendor/**',
        wrapper: function () {}
    });
});
